INSERT INTO products (title, price, photoUrl, description) VALUES
(1, 'Чікен бургер', 67.00, 'https://lh3.googleusercontent.com/d/1jA66msjb0I1BPS7OU-IAmxz23rz8872e=h300', 'Соковита котлета під сиром Hochland з листям салату та карамелізованю цибулею. Все це в ніжних булочках з смачною котлетою, приготована до золотистої скоринки, покрита ароматним сиром Hochland. Додаємо хрустку зелень салату та карамелізовану цибулю для неперевершеного смаку. Все це між двома пухкими булочками, посипаними кунжутом, із додаванням ніжного соусу Айолі.'),
(2, 'Гриль бургер', 70.00, 'https://lh3.googleusercontent.com/d/1QX0pH43FqCB4HktppPCCUEu_8yx3SECq=h300', 'Соковите куряче філе обсмажене на грилі в поєднанні з курячою котлетою, листя свіжого салату та скибочка помідору, заправлені вишуканим соусом Франсуа на булочці з кунжутом. Найкраще втамує голод - швидко та смачно.'),
(3, 'Роял Делюкс', 115.00, 'https://lh3.googleusercontent.com/d/1hgIeis5se7bw1YHFkLt-f68D6IUZLMPb=h300', 'Яловича котлетка, огорнута сиром чедер, а також свіжий салат, томат, мариновані огірочки, цибуля та мікс гірчиці, кетчупа й соуса створюють незабутній смак.'),
(4, 'Black бургер', 129.00, 'https://lh3.googleusercontent.com/d/1J7-5S620DpQRxRs8fwaueElDpy71CyiU=h300', 'Булочка з чорнилом каракатиці, соус тартар, соус гірчичний, цибуля маринована, огірок маринований, листя салату, сир чедер, котлета свинна власного приготування.'),
(5, 'Black бургер з беконом', 150.00, 'https://lh3.googleusercontent.com/d/1ACtvaw85vvOi_lcAy0I2HVnS7S1DGYQJ=h300', 'Булочка з чорнилом каракатиці, соус тартар, соус гірчичний, цибуля маринована, огірок маринований, листя салату, помідори, бекон, сир чедер, котлета свинна власного приготування.'),
(6, 'Чікен бургер з беконом', 97.00, 'https://lh3.googleusercontent.com/d/1YIzsVmBA8zbm6K1PGi_Ksu0lP-KENyxD=h300', 'Соковита котлета під сиром Hochland з листям салату та карамелізованю цибулею. Все це в ніжних булочках з смачною котлетою, приготована до золотистої скоринки, покрита ароматним сиром Hochland. Додаємо хрустку зелень салату та карамелізовану цибулю, не забуваємо про бекон для неперевершеного смаку. Все це між двома пухкими булочками, посипаними кунжутом, із додаванням ніжного соусу Айолі.'),
(7, 'Воппер', 50.00, 'https://lh3.googleusercontent.com/d/1LlNzae2jcQOa2rTuUjMUW_T3ePuCzlWG=h300', 'Цей апетитний бургер складається з соковитої котлети з гриля, покритої розтопленим сиром, що стікає на шар смаженої картоплі. Все це знаходиться між двома м’якими булочками з кунжутом. Цей бургер не лише смачний, а й візуально привабливий!'),
(8, 'Біг бургер', 145.00, 'https://lh3.googleusercontent.com/d/1xGpLRcZaj1r7yCnR7iKjYdS1hxaSwMSx=h300', 'Цей ситний двійний бургер має дві котлети з яловичини, покриті розтопленим сиром, що надає бургеру м’якості та кремовості. Під котлетами видно нарізані огірки, які мають яскравий зелений колір та глянцеву текстуру. Зверху додано свіжий та хрусткий салат, змішаний з соусом, що робить його кремовим.'),
(9, 'Дабл чіз бургер', 200.00, 'https://lh3.googleusercontent.com/d/1t2HQ3GKgVgSVGI3iHgrjKULW9047GGkS=h300', '«Кожен, хто любить хороший бургер, має слабкість до Дабл чіза», — каже наш шеф-кухар. У своїй оді класичному фаст-фуду він замінює середню булочку на хрусткі смажені зелені помідори, які вбирають усі пікантні соки яловичини, плавленого сиру та солодкого майонезу чилі.'),
(10, 'Бургер Філадельфія', 210.00, 'https://lh3.googleusercontent.com/d/1LlNzae2jcQOa2rTuUjMUW_T3ePuCzlWG=h300', 'Всередині бургеру знаходиться м’який та липкий білий рис, що обрамляє яскраві інгредієнти: оранжеву ікру риби, скибочки зеленого авокадо та кремову начинку, що може бути майонезом або сиром крем-чіз. Цей бургер може стати чудовим доповненням до вашого столу, пропонуючи не лише смакову насолоду, а й візуальну привабливість.'),
(11, 'Чікен рол', 90.00, 'https://lh3.googleusercontent.com/d/1SYR1iLraKgJ_TPIzIsYh9fiECy5DyTcT=h300', 'Загорнуті в тонкий лаваш шматочки курятини, свіжі помідори та хрумкий салат-латук – і все це заправлене пікантним гірчично-медовим соусом.'),
(12, 'Дабл чікен рол', 100.00, 'https://lh3.googleusercontent.com/d/1HpInr5TCGsmSsvzeGcdZsxlpWamakLr3=h300', 'Загорнуті в тонкий лаваш шматочки курятини, свіжі помідори та хрумкий салат-латук – і все це заправлене пікантним гірчично-медовим соусом.'),
(13, 'Рол з креветками', 95.00, 'https://lh3.googleusercontent.com/d/1K4Fo8ZAk0O_6Tk822fwm5tqRk21lbIjk=h300', 'Овочевий мікс з капусти та шматочків маринованих огірків, в поєднанні з креветками в паніровці, загорнутий в грузинський лаваш, що заправлений особливим соусом кетчуп.'),
(14, 'Фіш рол', 95.00, 'https://lh3.googleusercontent.com/d/1CPeHO0LIB0oMahiuws82gwkC2-vQJTZr=h300', 'Свіжий смак овочів у поєднанні з соковитим філе риби в паніруванні. Страва для справжніх гурманів!'),
(15, 'Рол Техас', 100.00, 'https://lh3.googleusercontent.com/d/1fxThB-xhPuGiugP9B2PMcPNqK712dI7f=h300', 'Цей смачний рол наповнений свіжими інгредієнтами: хрустким салатом, сочними помідорами, відбивною та танучим сиром. Все це загорнуто в м’який, легко підсмажений лаваш, що робить його ідеальним для смачного обіду!'),
(16, 'Рол Гурман', 120.00, 'https://lh3.googleusercontent.com/d/12Q0KnKI-C2LW9ESwAHbAQrhm8DdKvVgn=h300', 'Це апетитний рол, який поєднує в собі соковиту смажену ковбаску, обернуту в м’який лаваш, з хрусткими чіпсами та танучим сиром, все це доповнено ароматним кетчупом, що робить страву неймовірно смачною та задовольняє найвибагливіші смаки. Відмінний вибір для швидкого перекусу або ситного обіду.'),
(17, 'Нагетси', 50.00, 'https://lh3.googleusercontent.com/d/19iBeEpdhFaELm0VVoBI5C6rzMr5WAflT=h300', 'Нагетси - це популярна страва, яка виготовляється з курячого м’яса. М’ясо формується у невеликі шматочки, панірується та смажиться до золотистого кольору. Вони мають хрустку зовнішню оболонку та ніжне, сочне м’ясо всередині.'),
(18, 'Баффало', 70.00, 'https://lh3.googleusercontent.com/d/1s6R1Bwohto7O3EoK_JuArXKfs6NF-GZc=h300', 'Хрусткі курячі крильця в гострому соусі. Спробуй щось нове.'),
(19, 'Спайсі Стріпс', 80.00, 'https://lh3.googleusercontent.com/d/13w2q0m0imUyfQAUqTx89zHvyTotHTMjA=h300', 'Куряче філе обсмажене в особливій паніровці власного приготування.'),
(20, 'Рибні палички', 80.00, 'https://lh3.googleusercontent.com/d/1zY-XhT19NUbZaRYrD_Er-9hiN5yX7o3X=h300', 'Рибні палички - це популярна страва, яка виготовляється з рибного філе. Рибне філе розрізається на палички, панірується та смажиться до золотистого кольору. Вони мають хрустку зовнішню оболонку та ніжне, сочне рибне м’ясо всередині.'),
(21, 'Сирні палички', 87.00, 'https://lh3.googleusercontent.com/d/1jlx6GqmKH5fYIJeMjRFbZBzJvsDJjFeh=h300', 'Ніжна моцарела обсмажена в хрусткій паніровці. Справді смачний сирний смак.'),
(22, 'Крила Кріспі', 90.00, 'https://lh3.googleusercontent.com/d/1bXekhRkDfPAIisqPqkjS9QaIZbdgL2Jz=h300', 'Крила Кріспі - це смажені курячі крильця, які мають хрустку зовнішню оболонку та ніжне, сочне м’ясо всередині. Вони готуються за спеціальною технологією, яка дозволяє досягти ідеальної хрусткості.'),
(23, 'Сонячний Бліс', 60.00, 'https://lh3.googleusercontent.com/d/1JI3sh-mw6RBsQ1uR3O6BgkZrlV5QTzBV=h300', 'Цей торт має яскравий помаранчевий колір, що нагадує про соковиті цитрусові плоди. Він складається з ніжного бісквіту, просоченого ароматним апельсиновим сиропом. Верхній шар торта покритий глянсовою помаранчевою глазур’ю, яка додає торту елегантності. Завершує композицію шматочок вершкового крему, прикрашений червоною вишнею.'),
(24, 'Шоколадний Рай', 75.00, 'https://lh3.googleusercontent.com/d/1kbq2zj3S3WgeztPMpA7jieLgtqy57GOL=h300', 'Цей торт - справжній шедевр для шоколадних гурманів. Він складається з багатошарового бісквіту, просоченого сиропом, що чергується зі світлими та темними шарами, створюючи гармонію смаків. Поверхня торта покрита блискучою шоколадною глазур’ю, а зверху прикрашена мигдалем, черешнями та свіжою м’ятою.'),
(25, 'Шоколадний торт', 50.00, 'https://lh3.googleusercontent.com/d/1-D4j8OxyhKCd2s7IuSPeJxYuhA-aOXvu=h300', 'Цей торт - наче витвір мистецтва, що вабить своїм багатим шоколадним ароматом. Він складається з пишних шарів темного шоколадного бісквіту, просочених солодким сиропом для додаткової вологості та смаку. Верхівка торта прикрашена густою шоколадною глазур’ю, яка надає йому вишуканого блиску.'),
(26, 'Полуничний Сюрприз', 90.00, 'https://lh3.googleusercontent.com/d/1yb8F-Io22aQ5kf4xB6Ljdf9OUb5yxK41=h300', 'Цей торт - справжнє втілення літньої свіжості. “Полуничний Сюрприз” складається з повітряних шарів бісквіту, просочених легким кремом. Верх торта прикрашений пишними хмарками збитих вершків та свіжими, соковитими полуницями, що додають яскравості та ніжності.'),
(27, 'Ягідний Вінчестер', 70.00, 'https://lh3.googleusercontent.com/d/1I8lq3jN967K4F6PuLpbm4hc3Yn24Gp7c=h300', 'Цей торт - справжній витвір кулінарного мистецтва, що здатен зачарувати своїм виглядом та смаком. “Ягідний Вінчестер” складається з легких бісквітних шарів, просочених солодким кремом, і вінчається глянцевим ягідним желе. Кожен шматочок торта прикрашений свіжою малиною та ніжним листочком м’яти, додаючи елегантності та свіжості.'),
(28, 'Шоколадний мафін', 30.00, 'https://lh3.googleusercontent.com/d/10w3kvKlYEyrlrsKm-9F1Ln9j4FPaLZGZ=h300', 'Це чудовий мафін з шоколадним бісквітом, який зачаровує своїм виглядом та смаком. Верхівка мафіна прикрашена рожевим кремом, що має ніжний відтінок та апетитний вигляд, а чорні зірочки на вершині додають вишуканості.'),
(29, 'Ванільний мафін', 30.00, 'https://lh3.googleusercontent.com/d/1FrPGD68QpphsyxXz4w7h0wniIjaMqJI6=h300', 'Цей мафін - справжнє втілення ніжності та вишуканості. Він має ванільну бісквітну основу, що дарує глибокий смак, а верхівка прикрашена рожевим кремом, який виглядає як м’яка хмаринка. Білі зірочки на кремі додають чарівності, а рожева формочка з білими крапочками підкреслює загальний стиль.'),
(30, 'Ванільне морозиво', 45.00, 'https://lh3.googleusercontent.com/d/1wpGOUN6izFKWEiTpBwWghhVzQIL-POAU=h300', 'Це морозиво в ріжку - справжній символ літнього задоволення. Воно має ніжну ванільну основу, що м’яко тане в роті, та хрусткий золотистий вафельний ріжок, який додає текстурного контрасту.'),
(31, 'Шоколадне морозиво', 45.00, 'https://lh3.googleusercontent.com/d/1H2vfwQkMqGyWqd2ICtFTFdWYxV3IU9lI=h300', 'Це морозиво в ріжку - справжній символ літнього задоволення. Воно має ніжну шоколадну основу, що м’яко тане в роті, та хрусткий золотистий вафельний ріжок, який додає текстурного контрасту.'),
(32, 'Соус Кетчуп', 15.00, 'https://lh3.googleusercontent.com/d/1DaIA8-PXaUJJNdGNALm9L_9s11k9SGOg=h300', 'Кетчуп - це популярний соус, який використовується у всьому світі. Він має густу текстуру та яскравий червоний колір.'),
(33, 'Соус Майонез', 15.00, 'https://lh3.googleusercontent.com/d/1MwkOR09mdWOUWwFflzkrfr0JGesnahvM=h300', 'Майонез - це популярний соус, який використовується у всьому світі. Він має густу, кремову текстуру та світло-жовтий колір.'),
(34, 'Соус Барбекю', 15.00, 'https://lh3.googleusercontent.com/d/1jkhn86UYgZgUlPI_gDHXdZf3HOHMzUoZ=h300', 'Соус має темний, насичений колір, що свідчить про його багатий смаковий профіль з нотами копченості та солодкості.'),
(35, 'Соус Сирний', 15.00, 'https://lh3.googleusercontent.com/d/1CSgAlZ6zXaEZ_m62_iTgedWNqLYauvLf=h300', 'Сирний соус - це кремовий та багатий на смак соус, який використовується у різних кулінарних стравах. Він виготовляється з розтопленого сиру, часто з додаванням молока, вершків або бульйону для досягнення бажаної консистенції.'),
(36, 'Соус Гірчиця', 15.00, 'https://lh3.googleusercontent.com/d/1HbhtOdjrDseE7x_mn3lsCNgT-HqY4ajd=h300', 'Гірчиця - це популярний соус, який використовується у всьому світі. Вона має густу текстуру та яскравий жовтий колір.'),
(37, 'Соус Цезар', 15.00, 'https://lh3.googleusercontent.com/d/1FqW76OS0pnXHmwHuMMQw4XxjKMTFzd1V=h300', 'Основні інгредієнти соусу Цезар включають оливкову олію, жовтки вівсянки, лимонний сік, часник, анчоуси, пармезан та вустерширський соус. Всі ці інгредієнти змішуються до гладкої консистенції, створюючи кремовий соус, який додає салату Цезар неповторний смак.'),
(38, 'Соус Каррі', 15.00, 'https://lh3.googleusercontent.com/d/1qDrOh9tplZdIHCAUiBkt-PpSJMaFGQDL=h300', 'Основними інгредієнтами соусу каррі є каррі-порошок або паста каррі, які містять багатий мікс спецій, включаючи коріандр, турмерик, кумін, фенхель та інші.'),
(39, 'Кока-Кола', 45.00, 'https://lh3.googleusercontent.com/d/1LyoTpfQr_2FDk0e_PskF-1qzDwMyOYX4=h300', 'Кока-кола - це газований безалкогольний прохолоджувальний напій.'),
(40, 'Фанта', 45.00, 'https://lh3.googleusercontent.com/d/1Lsj7pfvHItnOHI7os1greQMbVJQGPFS3=h300', 'Фанта - це газований безалкогольний прохолоджувальний напій.'),
(41, 'Живчик', 45.00, 'https://lh3.googleusercontent.com/d/1jNL0bMU1fLmGy84NF9b9kIJfyaw_198d=h300', 'Живчик - це газований безалкогольний прохолоджувальний напій.'),
(42, 'Пепсі', 35.00, 'https://lh3.googleusercontent.com/d/1CrEStqEQwtRX6knhIPXNodSIuL5mRz5c=h300', 'Пепсі - це газований безалкогольний прохолоджувальний напій.'),
(43, 'Лате', 45.00, 'https://lh3.googleusercontent.com/d/1gkd6w8JVv29YVfSEDJ9OjvGFKk8Nw6Ln=h300', 'Це дуже смачний та ароматний напій, який вживають у гарячому вигляді.'),
(44, 'Капучіно', 55.00, 'https://lh3.googleusercontent.com/d/1tbBbVRKvmg41Tiar_OM3IFWPq_juEAoP=h300', 'Це дуже смачний та ароматний напій, який вживають у гарячому вигляді.'),
(45, 'Чай', 35.00, 'https://lh3.googleusercontent.com/d/1bNYsbCR0Ino21PVlP6STjYujADZf2lyO=h300', 'Це дуже смачний та ароматний напій, який вживають у гарячому вигляді.'),
